class ChatbotTrainer:
    def __init__(self):
        self.model = None
        self.tokenizer = None
        self.model_dir = "./chatbot_model"
        self.conversation_file = "./conversation_data.json"
        self.last_trained = None
        self.conversation_history = []
        self.load_memory()
        self.initialize_model()  # Ensure model is loaded on startup


        
    def initialize_model(self):
        """Initialize GPT-2 model and tokenizer with proper padding token."""
        self.tokenizer = GPT2Tokenizer.from_pretrained("gpt2")
        self.tokenizer.pad_token = self.tokenizer.eos_token
        self.model = GPT2LMHeadModel.from_pretrained("gpt2")
        
    def load_conversation_data(self):
        """Load existing conversation data from file."""
        if os.path.exists(self.conversation_file):
            with open(self.conversation_file, 'r') as f:
                return json.load(f)
        return []

    def save_conversation_data(self, data):
        """Save conversation data to file."""
        with open(self.conversation_file, 'w') as f:
            json.dump(data, f)

    def add_conversation(self, user_input, bot_response):
        """Add a new conversation to the training data."""
        conversations = self.load_conversation_data()
        conversations.append({
            "input": user_input,
            "output": bot_response,
            "timestamp": datetime.now().isoformat()
        })
        self.save_conversation_data(conversations)

