    def prepare_data(self):
        """ Combine conversation and poem datasets."""
        conversation_dataset = self.prepare_conversation_data()
        poem_dataset = self.prepare_poem_data()
        
        # Combine datasets
        combined_dataset = concatenate_datasets([conversation_dataset, poem_dataset])
        
        # Tokenize the combined dataset
        tokenized_dataset = combined_dataset.map(
            lambda examples: self.tokenizer(
                examples["text"], 
                truncation=True, 
                padding="max_length",  # Use max_length for consistent padding
                max_length=100
            ),
            batched=True
        )
        
        # Add labels for training
        tokenized_dataset = tokenized_dataset.map(
            lambda examples: {"labels": examples["input_ids"]},
            batched=True
        )
        
        return tokenized_dataset
    
    def train(self):
        """Train the chatbot model."""
        self.initialize_model()
        tokenized_dataset = self.prepare_data()
        
        training_args = TrainingArguments(
            output_dir=self.model_dir,
            num_train_epochs=3,
            per_device_train_batch_size=2,
            warmup_steps=500,
            weight_decay=0.01,
            logging_dir='./logs',
            logging_steps=10,
            save_strategy="epoch",
            prediction_loss_only=True
        )
        
        trainer = Trainer(
            model=self.model,
            args=training_args,
            train_dataset=tokenized_dataset
        )
        
        print("Starting model training...")
        trainer.train()
        self.save_model()
        self.last_trained = datetime.now()
        print("Training complete!")
        
    def save_model(self):
        """Save the trained model and tokenizer."""
        if not os.path.exists(self.model_dir):
            os.makedirs(self.model_dir)
        self.model.save_pretrained(self.model_dir)
        self.tokenizer.save_pretrained(self.model_dir)
        
    def load_model(self):
        """Load trained model from disk."""
        if os.path.exists(self.model_dir):
            self.tokenizer = GPT2Tokenizer.from_pretrained(self.model_dir)
            self.model = GPT2LMHeadModel.from_pretrained(self.model_dir)
            return True
        return False
    
