    def generate_response(self, user_input):
        if not self.tokenizer or not self.model:
            return "Error: Model not loaded. Please initialize the model first."
        
        if not isinstance(user_input, str) or not user_input.strip():
            return "Error: Invalid input. Please enter a valid message."

        self.conversation_history.append(f"User: {user_input.strip()}")

        # Maintain conversation history within token limit
        while len("\n".join(self.conversation_history)) > 512:
            self.conversation_history.pop(0)

        prompt = "\n".join(self.conversation_history) + "\nBot:"
        
        inputs = self.tokenizer(prompt, return_tensors="pt", truncation=True, max_length=512, padding=True)

        # Move input tensors to the same device as the model
        device = next(self.model.parameters()).device
        inputs = {key: value.to(device) for key, value in inputs.items()}

        outputs = self.model.generate(
            **inputs,
            max_length=200,  
            temperature=0.7,  
            top_p=0.9,  
            no_repeat_ngram_size=2,
            pad_token_id=self.tokenizer.eos_token_id
        )

        response = self.tokenizer.decode(outputs[0], skip_special_tokens=True)

        # Extract only the bot's response
        bot_response = response.split("Bot:")[-1].strip() if "Bot:" in response else response.strip()

        self.conversation_history.append(f"Bot: {bot_response}")

        return bot_response if bot_response else "Error: Failed to generate a response."


    def save_memory(self, file_path="conversation_memory.json"):
        with open(file_path, "w") as f:
            json.dump(self.conversation_history, f)
        
    def load_memory(self, file_path="conversation_memory.json"):
        try:
            with open(file_path, "r") as f:
                self.conversation_history = json.load(f)
        except FileNotFoundError:
            self.conversation_history = []


